import React, { Component } from 'react';

export default class MyComp extends Component {
  constructor(props) {
    super(props);
    this.state = { favoritecolor: 'yellow', age: 26, name: 'Akshay' };
  }

  static getDerivedStateFromProps(props, state) {
    // Example: Update favoritecolor based on props
    if (props.favoritecolor !== state.favoritecolor) {
      return {
        favoritecolor: props.favoritecolor
      };
    }
    return null;
  }

  componentDidMount() {
    console.log("This is componentDidMount");
  }

  render() {
    console.log("This is Render");
    return (
      <div>
        <h1>My Favorite Color is {this.state.favoritecolor}</h1>
        <button onClick={() => { this.setState({ age: 50, name: 'Javal', favoritecolor: 'blue' }) }}>Click Me!</button>
      </div>
    );
  }
}
