class APIStorage {
    inProgress = true;
    data = null;

    doFetch = async function () {
        const rowData = await fetch("https://jsonplaceholder.typicode.com/photos/")
        const jsonData = await rowData.json()

        return jsonData;
    }

    start = function () {
        if (this.inProgress) {
            return this.doFetch().then((res) => {
                this.inProgress = false;
                this.data = res;
                return res;
            }).catch((err) => {
                this.inProgress = false;
                this.data = err;
                throw err;
            });
        } else {
            if (this.data instanceof Error) {
                throw this.data;
            } else {
                return Promise.resolve(this.data);
            }
        }
    }
}

const z = new APIStorage();

export default function LazyComp() {
    z.start().catch((err) => {
        console.error("Error:", err);
    });

    return (
        <div>
            <h1> Loading... </h1>
        </div>
    );
}
