class APIStorage {
    inProgress = true;
    data = null;

    doFetch = async function () {
        const rowData = await fetch("https://jsonplaceholder.typicode.com/photos/");
        const jsonData = await rowData.json();

        return jsonData;
    };

    start = async function () {
        try {
            const res = await this.doFetch();
            this.inProgress = false;
            this.data = res;
            return res;
        } catch (err) {
            this.inProgress = false;
            this.data = err;
            throw err;
        }
    };
}

const z = new APIStorage();

export default function LazyComp() {
    z.start().catch((err) => {
        console.error("Error:", err);
    });

    return (
        <div>
            <h1> Loading... </h1>
        </div>
    );
}
